const clientId = 'db3a8874dd2d4eda92758c0e163dc98d';
//const clientSecret = '2eb212bba09640fa9e08432ca4ad9b4a';
const redirectURI = 'http://localhost:3000';
let accessToken = '';

const Spotify = {
  expirationTime: '',
  getAccessTokenFromURL() {
    console.log("getAccessTokenFromURL");
    const currentURL = new URL(window.location.href);
    console.log(currentURL);
    this.expirationTime = currentURL.searchParams.get("expires_in");
    return currentURL.searchParams.get("access_token");
  },
  getAccessToken() {
    if (accessToken || this.getAccessTokenFromURL()) {
      console.log("Found it!");
      return accessToken;
    } else {
      console.log("Needs access token");
      window.location.replace(`https://accounts.spotify.com/authorize?client_id=${clientId}&response_type=token&scope=playlist-modify-public&redirect_uri=${redirectURI}`);
    }
  },
  search(term) {
    accessToken = this.getAccessToken();
    console.log(accessToken);

    return fetch(`https://api.spotify.com/v1/search?type=track&q=${term}`, {
      header: {
        Authorisation: `Bearer ${accessToken}`
      }
    }).then(response => {
      if (response.ok) {
        return response.json();
      }
      throw new Error('Request Failed!');
    }, networkError => {
      console.log(networkError.message);
    }).then(jsonResponse => {
      if(jsonResponse.tracks) {
        return jsonResponse.tracks.map(track => ({
          id: track.id,
          name: track.name,
          artist: track.artists[0].name,
          album: track.album.name,
          uri: track.uri
        }));
      }
    });
  }
};

export default Spotify;
